import React from "react";
import {Box , Typography , Button , Card , CardMedia , CardContent , CardActions , Chip} from '@mui/material';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import PhoneIcon from '@mui/icons-material/Phone';
import Rating from '@mui/material/Rating';
import { styled } from '@mui/system';

const ChipContainer = styled(Chip)({
    margin: '5px 5px 5px 0',
  });

const SubtitleContainer = styled(Typography)({
    display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginTop: '10px',
});

const SpacingContainer = styled(Typography)({
    display: 'flex', alignItems: 'center', justifyContent: 'space-between',
});

const PlaceDetails = ({place , selected , refProp}) => {

    if (selected) refProp?.current?.scrollIntoView({ behavior: 'smooth', block: 'start' });
    return (
        <Card elevation={6}>
            <CardMedia 
                style={{height: 200}}
                image={place.photo? place.photo.images.large.url : '/restaurant.jpg'}
                title={place.name} 
            />
            <CardContent>
                <Typography gutterBottom variant="h5">{place.name}</Typography>
                <Box display="flex" justifyContent="space-between" my={2}>
                    <Rating name="read-only" value={Number(place.rating)} readOnly />
                    <Typography component="legend">{place.num_reviews} review{place.num_reviews > 1 && 's'}</Typography>
                </Box>
                <Box display="flex" justifyContent="space-between">
                    <Typography component="legend">Price</Typography>
                    <Typography gutterBottom variant="subtitle1">
                        {place.price_level}
                    </Typography>
                </Box>
                <Box display="flex" justifyContent="space-between">
                    <Typography component="legend">Ranking</Typography>
                    <Typography gutterBottom variant="subtitle1">
                        {place.ranking}
                    </Typography>
                </Box>
                {place?.cuisine?.map(({ name }) => (
                    <ChipContainer key={name} size="small" label={name}/>
                ))}
                {place.address && (
                    <SubtitleContainer gutterBottom variant="body2" color="textSecondary">
                        <LocationOnIcon />{place.address}
                    </SubtitleContainer>
                )}
                {place.phone && (
                    <SpacingContainer variant="body2" color="textSecondary">
                        <PhoneIcon /> {place.phone}
                    </SpacingContainer>
                )}
            </CardContent>
            <CardActions>
            <Button size="small" color="primary" onClick={() => window.open(place.web_url, '_blank')}>
                Trip Advisor
            </Button>
            <Button size="small" color="primary" onClick={() => window.open(place.website, '_blank')}>
                Website
            </Button>
            </CardActions>
        </Card>
    );
}

export default PlaceDetails;
