import React from "react";
import GoogleMapReact from 'google-map-react';
import { styled } from '@mui/system'; // Import styled from @mui/system
import { useMediaQuery, Paper, Typography } from '@mui/material';
import LocationOnOutlinedIcon from '@mui/icons-material/LocationOnOutlined';
import Rating from '@mui/material/Rating';

const MapContainer = styled('div')({
  height: '85vh',
  width: '100%',
});

const MarkerContainer = styled('div')({
  position: 'absolute', transform: 'translate(-50%, -50%)', zIndex: 1, '&:hover': { zIndex: 2 },
});

const PaperContainer = styled('Paper')({
  padding: '10px', display: 'flex', flexDirection: 'column', justifyContent: 'center', width: '100px',
});

const TypographyContainer = styled('Typography')({

});

const PointerContainer = styled('img')({
  cursor: 'pointer',
});

const Map = ({setCoordinates, setBounds, coordinates , places}) => {
  const isDesktop = useMediaQuery('(min-width:600px');

  return (
    <MapContainer>
      <GoogleMapReact
        bootstrapURLKeys={{ key: 'AIzaSyCSlzGnDgY6QamtwsGKmfC76bfJ_E7Z07U' }} // Replace with your API key
        defaultCenter={coordinates}
        center={coordinates}
        defaultZoom={14}
        margin={[50, 50, 50, 50]}
        options={''}
        onChange={(e) => {
          console.log(e);
          setCoordinates({ lat: e.center.lat, lng: e.center.lng});
          setBounds({ ne : e.bounds.ne , sw: e.bounds.sw});
        }}
        onChildClick={''}
      >
          {Array.isArray(places) &&
          places.length > 0 && places.map((place, i) => (
            <MarkerContainer 
              lat={Number(place.latitude)}
              lng={Number(place.longitude)}
              key={i}
            >
            {
              !isDesktop ? (
                <LocationOnOutlinedIcon color="primary" fontSize="large"/>
              ) : (
                <PaperContainer elevation={3}>
                  <TypographyContainer variaant="subtitle2" gutterBottom>
                    {place.name}
                  </TypographyContainer>
                  <PointerContainer 
                  src = {place.photo? place.photo.images.large.url : 'https://www.foodserviceandhospitality.com/wp-content/uploads/2016/09/Restaurant-Placeholder-001.jpg'}
                  alt={place.name}/>
                </PaperContainer>
              )
            }
          </MarkerContainer >
        ))}
      </GoogleMapReact>
    </MapContainer>
  );
};

export default Map;
