import React from "react";
import GoogleMapReact from 'google-map-react';
import { styled } from '@mui/system'; // Import styled from @mui/system
import { useMediaQuery, Paper, Typography } from '@mui/material';
import LocationOnOutlinedIcon from '@mui/icons-material/LocationOnOutlined';
import Rating from '@mui/material/Rating';

const MapContainer = styled('div')({
  marginTop: '40px',
  height: '85vh',
  width: '100%',
});

const MarkerContainer = styled('div')({
  position: 'absolute',
  transform: 'translate(-50%, -50%)',
  zIndex: 1,
  '&:hover': {
    zIndex: 2,
    // Add any additional styling for the hover effect
  },
});

const PaperContainer = styled('Paper')({
  padding: '10px', 
  display: 'flex', 
  flexDirection: 'column', 
  justifyContent: 'center', 
  width: '100px',
  alignItems: 'center',
  textAlign: 'center',
});

const TypographyContainer = styled('Typography')({
  marginBottom: '8px',
});

const RatingContainer = styled('div')({
  display: 'flex',
  alignItems: 'center',  // Align ratings and icon in the center
});

const PointerContainer = styled('img')({
  cursor: 'pointer',
  maxWidth: '100%',
  maxHeight: '100%',
  position: 'absolute',
  top: '-50%', // Adjust as needed
  left: '-50%',
  borderRadius: '4px', 
});

const Map = ({setCoordinates, setBounds, coordinates , places , setChildClicked}) => {
  const isDesktop = useMediaQuery('(min-width:600px');
  return (
    <MapContainer>

      <GoogleMapReact
        bootstrapURLKeys={{ key: 'AIzaSyCSlzGnDgY6QamtwsGKmfC76bfJ_E7Z07U' }} // Replace with your API key
        defaultCenter={coordinates}
        center={coordinates}
        defaultZoom={10}
        margin={[50, 50, 50, 50]}
        options={''}
        onChange={(e) => {
          //console.log(e);
          setCoordinates({ lat: e.center.lat, lng: e.center.lng});
          setBounds({ ne : e.bounds.ne , sw: e.bounds.sw});
        }}
        onChildClick={(child) => setChildClicked(child)}
      >
          {
          places?.length && places.map((place, i) => (
            (place.latitude && place.longitude) ? 
            <MarkerContainer
              lat={place.latitude}
              lng={place.longitude}
              key={i}
            >
              <LocationOnOutlinedIcon
                color="primary"
                fontSize="large"
                style={{ display: isDesktop ? 'none' : 'block' }}
              />
              <PaperContainer
                elevation={3}
                style={{ display: isDesktop ? 'block' : 'none' }}
              >
                <TypographyContainer variant="subtitle2" gutterBottom>
                  {place.name}
                </TypographyContainer>
                <PointerContainer
                  src={
                    place.photo
                      ? place.photo.images.large.url
                      : 'https://www.foodserviceandhospitality.com/wp-content/uploads/2016/09/Restaurant-Placeholder-001.jpg'
                  }
                  alt={place.name}
                />
                <RatingContainer>
                  <Rating name="read-only" size="small" value={Number(place.rating)} readOnly />
                </RatingContainer>
              </PaperContainer>
            </MarkerContainer> : null
          ))}
      </GoogleMapReact>
    </MapContainer>
  );
};

export default Map;
