import React from "react";
import GoogleMapReact from 'google-map-react';
import { styled } from '@mui/system'; // Import styled from @mui/system
import { useMediaQuery, Paper, Typography } from '@mui/material';
import LocationOnOutlinedIcon from '@mui/icons-material/LocationOnOutlined';
import Rating from '@mui/material/Rating';

const MapContainer = styled('div')({
  height: '85vh',
  width: '100%',
});

const Map = ({setCoordinates, setBounds, coordinates}) => {
  const isMobile = useMediaQuery('(min-width:600px');

  return (
    <MapContainer>
      <GoogleMapReact
        bootstrapURLKeys={{ key: 'AIzaSyCSlzGnDgY6QamtwsGKmfC76bfJ_E7Z07U' }} // Replace with your API key
        defaultCenter={coordinates}
        center={coordinates}
        defaultZoom={14}
        margin={[50, 50, 50, 50]}
        options={''}
        onChange={(e) => {
          console.log(e);
          const bounds = e.bounds;
  if (bounds) {
    setCoordinates({ lat: e.center.lat, lng: e.center.lng });
    setBounds({
      nw: { lat: bounds.nw.lat, lng: bounds.nw.lng },
      se: { lat: bounds.se.lat, lng: bounds.se.lng },
      sw: { lat: bounds.sw.lat, lng: bounds.sw.lng },
      ne: { lat: bounds.ne.lat, lng: bounds.ne.lng },
    });
  }
        }}
        onChildClick={''}
      >
        {/* Add your map markers or other components here */}
      </GoogleMapReact>
    </MapContainer>
  );
};

export default Map;
